INSERT INTO `category` (`id`, `name_category`, `thumbnail`, `intro`) VALUES
(1, 'academic', 'https://vinuni.edu.vn/wp-content/uploads/2021/06/Cover-Academics-scaled.jpg', '<h3 class=\"h3 text-secondary mt-5 mb-3 font-weight-bold\" style=\"box-sizing: inherit; line-height: 1.3; font-size: 1.375rem; font-family: Montserrat, sans-serif; color: rgb(205, 60, 63) !important;\">ACADEMIC PROGRAMS AT VINUNIVERSITY</h3><div class=\"study-life-about mb-5 post-detail\" style=\"box-sizing: inherit; font-size: 1.375rem; color: rgb(62, 62, 62); font-family: Montserrat, sans-serif;\"><p class=\"x_MsoNormal\" style=\"box-sizing: inherit;\"><span lang=\"EN-US\" style=\"box-sizing: inherit; font-size: 16px;\">Emphasizing active-learning in a curriculum co-designed and validated by Cornell University and the University of Pennsylvania, nestled within a campus rated 5-star by QS for Facilities, Academic Development, and Inclusiveness, VinUniversity will prepare you to be successful in whichever field you pursue, not just in Vietnam but anywhere in the world.</span></p><p class=\"x_MsoNormal\" style=\"box-sizing: inherit;\"><span lang=\"EN-US\" style=\"box-sizing: inherit; font-size: 16px;\">You’ll graduate with a qualification based on the highest international standards in research and teaching, that will equip you with the skills you need for a successful career. To foster lifelong learning, you as students will be immersed in a broad spectrum of subjects in the natural sciences, social sciences, arts and humanities, and other co-curricular activities offered by the&nbsp;<a href=\"https://vinuni.edu.vn/faculty-of-arts-and-sciences/\" target=\"_blank\" rel=\"noopener noreferrer\" data-auth=\"NotApplicable\" data-linkindex=\"7\" style=\"box-sizing: inherit; color: rgb(205, 60, 63);\">Faculty of Arts and Sciences</a>. This is the first home for all students as they embark on a journey of self-discovery, cultivates six attributes for success: Character, Competence, Critical Thinking, Leadership, Entrepreneurship, and National Pride-Global Awareness.</span></p><p style=\"box-sizing: inherit;\"><span style=\"box-sizing: inherit; font-size: 16px;\">VinUniversity has three Colleges and offers a range of degree programs as follow:</span></p></div>'),
(2, 'research', '../../images/research.JPG', '<h3 class=\"font-weight-light mb-4\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5rem !important; font-weight: 300 !important; line-height: 1.3; color: rgb(62, 62, 62); font-size: 1.375rem;\"><p style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1rem; text-align: justify;\">An entrepreneurial mindset, a passion to create a better world, and a determination to forge paths through unchartered territories — our researchers and scholars will pursue twenty-first century, cutting-edge research that includes concepts as diverse as the economics of the sustainable coffee industry, service innovations in the hospitality industry, and advances in medical technology.</p></h3><h3 class=\"h3 text-secondary mt-5 mb-3 font-weight-bold\" style=\"box-sizing: inherit; line-height: 1.3; font-size: 1.375rem; font-family: Montserrat, sans-serif; color: rgb(205, 60, 63) !important;\"><section class=\"our-strategic-collaborators\" style=\"box-sizing: inherit;\"><div class=\"container text-center py-4 mb-4\" style=\"box-sizing: inherit; width: 1366px; max-width: 1366px;\"><div class=\"pt-3\" style=\"box-sizing: inherit;\"><a class=\"btn btn-vin text-uppercase\" href=\"https://vinuni.edu.vn/research/\" style=\"box-sizing: inherit; color: rgb(255, 255, 255); font-weight: 700; border-color: rgb(46, 82, 136); padding: 0px 3.0625rem; line-height: 3rem; border-radius: 0px; transition: all 0.6s ease 0s; background-position: 99% center; background-size: 500%; background-image: linear-gradient(45deg, transparent 50%, rgb(46, 82, 136) 0px);\">EXPLORE VINUNI RESEARCH</a></div></div></section><section class=\"\" style=\"box-sizing: inherit;\"><div class=\"container\" style=\"box-sizing: inherit; width: 1366px; max-width: 1366px;\"><div class=\"row pt-3\" style=\"box-sizing: inherit; color: rgb(62, 62, 62); font-size: 16px; font-weight: 400;\"><div class=\"col-12 mb-5\" style=\"box-sizing: inherit; width: 1366px; -webkit-box-flex: 0;\"><div class=\"news-event__item row align-items-center\" style=\"box-sizing: inherit; -webkit-box-align: center !important;\"><div class=\"col-12 col-lg-6\" style=\"box-sizing: inherit; width: 683px; -webkit-box-flex: 0;\"><div class=\"hover-image my-4\" style=\"box-sizing: inherit; position: relative; overflow: hidden;\"></div></div></div></div></div></div></section></h3><h2 class=\"h3 text-secondary text-capitalize font-weight-bold mb-1\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0.25rem !important; font-weight: 700 !important; line-height: 1.3; color: rgb(205, 60, 63) !important; font-size: 1.375rem; text-transform: capitalize !important; font-family: Montserrat, sans-serif; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"></h2>'),
(3, 'admission', 'https://vinuni.edu.vn/wp-content/uploads/2020/11/AT_7337-e1604913243424.jpg', '<h3 class=\"h3 text-secondary mt-5 mb-3 font-weight-bold\" style=\"box-sizing: inherit; line-height: 1.3; font-size: 1.375rem; font-family: Montserrat, sans-serif; color: rgb(205, 60, 63) !important;\">Admissions at VinUniversity</h3><h3 class=\"h3 text-secondary mt-5 mb-3 font-weight-bold\" style=\"box-sizing: inherit; line-height: 1.3; font-size: 1.375rem; font-family: Montserrat, sans-serif; color: rgb(205, 60, 63) !important;\"><div class=\"study-life-about mb-5 post-detail\" style=\"box-sizing: inherit; font-size: 1.375rem; color: rgb(62, 62, 62); font-weight: 400;\"><p style=\"box-sizing: inherit;\">Admissions at VinUniversity is designed with our student—you—in the mind. That is why our process is simple, thoughtful, and grants you a lot of space for self-expression. Our goal is to educate and train outstanding talents; therefore, the university is committed to provide generous scholarships and financial aid to deserving talented students.</p><p style=\"box-sizing: inherit;\">We are not a university of the privileged, but indeed a university of talents. Welcome to the first step in demonstrating your talent and realizing the commitment you bring to our community, Vietnam, and the world!</p></div></h3><h3 class=\"h3 text-secondary mt-5 mb-3 font-weight-bold\" style=\"box-sizing: inherit; line-height: 1.3; font-size: 1.375rem; font-family: Montserrat, sans-serif; color: rgb(205, 60, 63) !important;\"><section class=\"\" style=\"box-sizing: inherit;\"><div class=\"container\" style=\"box-sizing: inherit; width: 1366px; max-width: 1366px;\"><div class=\"row pt-3\" style=\"box-sizing: inherit; color: rgb(62, 62, 62); font-size: 16px; font-weight: 400;\"><div class=\"col-12 mb-5\" style=\"box-sizing: inherit; width: 1366px; -webkit-box-flex: 0;\"><div class=\"news-event__item row align-items-center\" style=\"box-sizing: inherit; -webkit-box-align: center !important;\"><div class=\"col-12 col-lg-6\" style=\"box-sizing: inherit; width: 683px; -webkit-box-flex: 0;\"><div class=\"hover-image my-4\" style=\"box-sizing: inherit; position: relative; overflow: hidden;\"></div></div></div></div></div></div></section></h3><h2 class=\"h3 text-secondary text-capitalize font-weight-bold mb-1\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0.25rem !important; font-weight: 700 !important; line-height: 1.3; color: rgb(205, 60, 63) !important; font-size: 1.375rem; text-transform: capitalize !important; font-family: Montserrat, sans-serif; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"></h2>'),
